import './styles.scss'
import Button from '../components/Button/Button';
import { Fragment } from 'react';
import Head from 'next/head';
import Router from 'next/router';
import { Link } from "react-scroll";
import React from "react";
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import TextField from '@material-ui/core/TextField';
import Slide from "@material-ui/core/Slide";
import InputAdornment from "@material-ui/core/InputAdornment";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import { makeStyles } from "@material-ui/core/styles";

const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="down" ref={ref} {...props} />;
  });
  

const Index = () => {

    const [open, setOpen] = React.useState(false);
  
    const handleClickOpen = () => {
      setOpen(true);
    };
  
    const handleClose = () => {
      setOpen(false);
    };

    
    const useStyles = makeStyles;
    const classes = useStyles;
    const [signupModal, setSignupModal] = React.useState(false);
    return (
        <Fragment>
            <Head>
                <title>JusCredit - Home</title>
            </Head>
            <main>
                <p>
                <div className="map-container"> 
                    <div className="map" />
                     <h1>Antecipe seu </h1>
                         <h1>crédito trabalhista</h1><h2>Têm um processo ganho? Receba agora</h2>       
                    <div className="buttons-container">
                    <Button onClick={()=> Router.push('/Modal')}color="primary">Quero antecipar</Button> 
                   <Button onClick={()=> Router.push('/')} color="secondary">Quero investir</Button>
                   <div>
      <button color="primary" onClick={() => setSignupModal(true)}>
         Signup
      </button>
      <Dialog
        
        open={signupModal}
        TransitionComponent={Transition}
        keepMounted
        onClose={() => setSignupModal(false)}
        aria-labelledby="signup-modal-slide-title"
        aria-describedby="signup-modal-slide-description"
      >
        <DialogTitle
            id="signup-modal-slide-title"
            disableTypography
           
          >
            <button
              
              key="close"
              aria-label="Close"
              onClick={() => setSignupModal(false)}
            >
              {" "}
              <Close  />
            </button>
            <h5 >
              Register
            </h5>
          </DialogTitle>
          <DialogContent
            id="signup-modal-slide-description"
           
          >
            <form>
                  <CustomInput
                    formControlProps={{
                      fullWidth: true,
                      
                    }}
                    inputProps={{
                      startAdornment: (
                        <InputAdornment
                          position="start"
                         
                        >
                      
                        </InputAdornment>
                      ),
                      placeholder: "First Name..."
                    }}
                  />
                  <CustomInput
                    formControlProps={{
                      fullWidth: true,
                    
                    }}
                    inputProps={{
                      startAdornment: (
                        <InputAdornment
                          position="start"
                           >
                          <Email  />
                        </InputAdornment>
                      ),
                      placeholder: "Email..."
                    }}
                  />
                  <CustomInput
                    formControlProps={{
                      fullWidth: true,
                     
                    }}
                    inputProps={{
                      startAdornment: (
                        <InputAdornment
                          position="start"
                         
                        >
                          
                        </InputAdornment>
                      ),
                      placeholder: "Password..."
                    }}
                  />
                  <FormControlLabel
                    classes={{
                      label: classes.label
                    }}
                    control={
                      <Checkbox
                        tabIndex={-1}
                        onClick={() => this.handleToggle(1)}
                     
                        
                        classes={{
                          checked: classes.checked,
                          root: classes.checkRoot
                        }}
                      />
                    }
                    label={
                      <span>
                        I agree to the <a href="#pablo">terms and conditions</a>
                        .
                      </span>
                    }
                  />
                  <div className={classes.textCenter}>
                    <button  color="primary">
                      Get started
                    </button>
                  </div>
                </form>
        
          </DialogContent>
      </Dialog>
    </div>
    
    <div className="cursor-container">
                 <Button onClick={()=> Router.push('/')} color="third" ><img src="/cursor.svg"/>
                 <Link to="antecipar" > 
                        <a className="link">Conheça as vantagens</a>
                        </Link></Button>
                        </div>
                </div>
                </div>
                <div className="antecipar">
               <div className="products-bg" /> 
                    <div> <h1>Mais valor para quem vende<br/>
                              Simples para quem investe</h1> 
               <h2>
                   Vantagens para quem vende:
               </h2>
                            <img src="/agilidade.svg"/>
                            <h2>
                            <div className="buttons-container"> 
                    <Button onClick={()=> Router.push('/')} color="sixty">Quero antecipar</Button>
</div>
                   Vantagens para quem investe:
               </h2>
                            <img src="/rentabilidade.svg"/>
                            <div className="buttons-container"> 
                    <Button onClick={()=> Router.push('/')} color="sixty">Quero investir</Button>
</div>       

               </div>
                     
                    <div className="funciona-container">
                    <div className="poligono-container"/>
                        <h1>Como funciona a plataforma?</h1>
                        <h2>Conte com toda a segurança, agilidade <br/>
                           e praticidade na liberação do seu processo ganho.
                        </h2>  
                        <img src="/trabalhador.svg"/>  
                        </div>
                <div className="duvidas-container">   
                    <h1>Têm dúvidas?</h1>
                     <h2>Queremos te ajudar a resolvê-las</h2>
                        <img src="/duvidas.svg"/>
                    
                    </div> 
                    <div className= "parceiros-container">
                        <h1> Seja um parceiro JusCredit</h1>
                        <h2>Indique créditos trabalhistas elegíveis e ganhe conosco.</h2>
        
<div>
      <Button  color="primary" onClick={handleClickOpen}>
        Indicar
      </Button>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Inscreva-se</DialogTitle>
        <DialogContent>
          <DialogContentText>
          Você receberá um email para criação de senha e completar seu perfil
          Iremos entrar em contato com você para confirmar suas informações.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Email @ "
            type="email"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancelar
          </Button>
          <Button onClick={handleClose} color="primary">
            Enviar
          </Button>
        </DialogActions>
      </Dialog>
    </div>

</div> 
           </div>   
                <br/>
                </p>
            </main>
        </Fragment >

    );
}
export default Index;
