import { useRef, useState, useEffect } from "react";
import { createPortal } from 'react-dom';
import CustomOverlayView from "../CustomOverlayView/CustomOverlayView";

function OverlayViewContainer(props) {
  const ref = useRef();
  const {
    0: mounted,
    1: setMounted
  } = useState(false);
  const {
    map,
    selector,
    children,
    position
  } = props;
  useEffect(() => {
    ref.current = document.querySelector(selector);
    setMounted(true);
  }, [selector]);
  let overlay = null;
  let el = null;

  if (map && position) {
    el = el || document.createElement('div');
    overlay = overlay || new CustomOverlayView({
      position,
      content: el
    });
    overlay.setMap(map);
    return mounted ? createPortal(children, el) : null;
  }

  return null;
}

export default OverlayViewContainer;
