import RegisterForm from "../../components/Register/RegisterForm";
import { useState } from "react";
import ConfirmationChoice from "../../components/Register/ConfirmationChoice/ConfirmationChoice";
import Confirmation from "../../components/Register/Confirmation/Confirmation";
import RegisterSuccess from "../../components/Register/RegisterSuccess/RegisterSuccess";

const SignupUser = () => {
    const [step, setStep] = useState(0)
    const [userValues, setUserValues] = useState(null)
    const [confirmationChoice, setConfirmationChoice] = useState(null);
    const [loading, setLoading] = useState(false);
    const [mask, setMask] = useState(Masks.CPF)

    const handleUserSubmit = (values) => {
        setUserValues(values)
        setStep(1)
    }

    const handleConfimationChoice = (choice) => {
        setConfirmationChoice(choice)
        setStep(2)
    }

    const handleConfimation = async (value) => {
        setLoading(true)
        await createUser({
            ...userValues
        })
        setStep(3)
    }
    const createUser = async (body) => {
        try {
            const res = await fetch('https://api.organis8.com.br/api/nao_produtores', { method: 'POST', body: JSON.stringify(body) })
            const data = await res.json();
            return data;
        }
        catch (e) {
            console.error(e)
        }
    }

    switch (step) {

        case 0:
            return <RegisterForm profile="user" onSubmit={handleUserSubmit} />
        case 1:
            return <ConfirmationChoice profile="user" values={userValues} onSubmit={handleConfimationChoice} />
        case 2:
            return <Confirmation loading={loading} profile="user" confirmationChoice={confirmationChoice} onSubmit={handleConfimation} />
        case 3:
            return <RegisterSuccess />
    }
    return (
        <main className="be-part-container">
            <img className="bg-left" src="/colored-products-left.svg" />
            <img className="bg-right" src="/be-part/bg.svg" />
            <div>
                <h1>Insira seu CPF ou CNPJ abaixo</h1>
                <label>
                    Se você é produtor e conhece o cadastro oficial do Ministério (MAPA) e acha que já faz parte, por favor, insira seu CPF ou CNPJ:
                </label>
                <StyledInput
                    mask={mask}
                    beforeMaskedValueChange={beforeMaskedValueChange}
                    placeholder="Ex: 111.111.111-11..."
                    value={documentInput}
                    onChange={handleChange}
                    className={userNotFound && 'not-found'}
                />
                {userNotFound && <span className="user-not-found">Cadastro não encontrado</span>}
                <Button color="primary" onClick={validateDocument} disabled={!documentInput.length} loading={loading.toString()} >Validar cadastro </Button>
                <Link href="/signup/producer"><a>Não estou cadastrado ></a></Link>
            </div>
            <img className="producer" src="/be-part/producer.svg" />
        </main>
    );



}
export default SignupUser
