import Link from "../Link/Link";
import './Header.scss'
import { useRouter } from "next/router";
import { useState, useEffect } from "react";


const Header = () => {
    const [openedMenu, setOpenedMenu] = useState(false)
    const [loggedIn, setLoggedIn] = useState(false);
    const router = useRouter()
    let themeClass = ''
    if (router && router.pathname !== '/') {
        themeClass = 'white-navbar'
    }
    useEffect(() => {
        if (document?.cookie) {
            if (document.cookie.includes('auth_token')) {
                setLoggedIn(true);
            } else {
                setLoggedIn(false);
            }
        }
    }, [router.pathname])
    useEffect(() => {
        setOpenedMenu(false)
    }, [router.pathname])
    const toggleMenu = () => {
        setOpenedMenu(!openedMenu)
    }
    return (
        <>
            <nav className={"navbar " + themeClass}>
            <a className="navbar-brand" href="/"> 
</a>      
                <img src="/logo.png" className="header-logo" />
                <div className="link-container">
                    <Link href="/">
                        <a className="link">Home</a>
                    </Link>
                    <Link href="/find-partners">
                        <a className="link">Encontrar produtores</a>
                    </Link>
                    <Link href="/be-part">
                        <a className="link">Faça parte</a>
                    </Link>
                    <Link href="/content">
                        <a className="link"> Conteúdo </a>
                    </Link>
                    <Link href="/contact">
                        <a className="link">Contato</a>
                    </Link>
                    <Link href="/profile">
                        <a className="link"> {loggedIn ? 'Meu perfil' : 'Login'}</a>
                    </Link>
                </div>
                <div className="menu-button" onClick={toggleMenu}>
                    <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24">
                        <path fill={themeClass === 'white-navbar' ? 'black' : 'white'} d="M24 6h-24v-4h24v4zm0 4h-24v4h24v-4zm0 8h-24v4h24v-4z" />
                    </svg>
                </div>
            </nav>
            {openedMenu && (
                <div className={"mobile-link-container " + themeClass}>
                    <Link href="/">
                        <a className="link">Home</a>
                    </Link>
                    <Link href="/find-partners">
                        <a className="link">Encontrar produtores</a>
                    </Link>
                    <Link href="/be-part">
                        <a className="link">Faça parte</a>
                    </Link>
                    <Link href="/content">
                        <a className="link"> Conteúdo </a>
                    </Link>
                    <Link href="/profile">
                        <a className="link">{loggedIn ? 'Meu perfil' : 'Login'}  </a>
                    </Link>
                    <Link href="/contact">
                        <a className="link">Contato</a>
                    </Link>
                </div>
            )
            }
        </>

    )
}
export default Header;
