import { Link, DirectLink, Element, Events, animateScroll as scroll, scrollSpy, scroller } from "react-scroll";
import './Header.scss'
import { useRouter, Router } from "next/router";
import { useState, useEffect, Component } from "react";

const Header = () => {
    const [openedMenu, setOpenedMenu] = useState(false)
    const router = useRouter()
    let themeClass = ''
    if (router && router.pathname !== '/') {
        themeClass = 'white-navbar'
    }
    const toggleMenu = () => {
        setOpenedMenu(!openedMenu)
    }    
    
return (
 <>
    <nav className={"nav" + themeClass}>
                      <a className="navbar-brand" href=""> <img src="/logo.svg" className="header-logo" />
</a>
                
    <div className="link-container">
  
      <Section
        title="Como funciona"
        subtitle={ComoFunciona}
        dark={true}
        id="como-funciona"
      />
      <Section
        title="Section 2"
        subtitle={dummyText}
        dark={false}
        id="section2"
      />
      <Section
        title="Section 3"
        subtitle={dummyText}
        dark={true}
        id="section3"
      />
      <Section
        title="Section 4"
        subtitle={dummyText}
        dark={false}
        id="section4"
      />
      <Section
        title="Section 5"
        subtitle={dummyText}
        dark={true}
        id="section5"
      />
    </div>

</nav>

{openedMenu && (
    <div className={"mobile-link-container " + themeClass}>
        <Link scroll href="/home">
            <a className="link">Home</a>
        </Link>
        <Link href="/find-partners">
            <a className="link">Encontrar produtores</a>
        </Link>
        <Link href="/be-part">
            <a className="link">Faça parte</a>
        </Link>
        <Link href="/content">
            <a className="link"> Conteúdo </a>
        </Link>
        <Link href="/profile">
            <a className="link">Meu Perfil  </a>
        </Link>
        <Link href="/contact">
            <a className="link">Contato</a>
        </Link>
    </div>
)
}
</>

)
}
      
      
export default Header;
